  const [filter, setFilter] = useState({ source: "All", tag: "All" });
  const [darkMode, setDarkMode] = useState(false);
  const [lists, setLists] = useState(["Follow Up", "Design Ideas", "Personal" ]);
  const [messages, setMessages] = useState([
    { id: 1, source: "Slack", sender: "Alex", content: "Alex: Can you share the Stripe payment metrics before standup?", timestamp: "9:22 AM", tags: ["work", "payments", "urgent"] },
    { id: 2, source: "Email", sender: "YC", content: "YC Team: Confirming your interview is set for Thursday, 10AM PST. Zoom link attached.", timestamp: "9:19 AM", tags: ["work", "schedule"] },
    { id: 3, source: "Instagram", sender: "Jenny", content: "Jenny: reposted a story — 'Everyone should vote today 🗳️'", timestamp: "9:15 AM", tags: ["personal"] },
    { id: 4, source: "Slack", sender: "Marco", content: "Marco: Final draft of the deck is in the shared folder. Let me know if it looks tight.", timestamp: "9:09 AM", tags: ["work", "design"] },
    { id: 5, source: "Email", sender: "Notion", content: "Notion: Weekly workspace summary is ready. You had 3 new pages shared.", timestamp: "9:05 AM", tags: ["tools"] },
    { id: 6, source: "Email", sender: "Notion", content: "Notion: Weekly workspace summary is ready. You had 3 new pages shared.", timestamp: "9:05 AM", tags: ["tools"] },
    { id: 7, source: "Email", sender: "Notion", content: "Notion: Weekly workspace summary is ready. You had 3 new pages shared.", timestamp: "9:05 AM", tags: ["tools"] },
    { id: 8, source: "Email", sender: "Notion", content: "Notion: Weekly workspace summary is ready. You had 3 new pages shared.", timestamp: "9:05 AM", tags: ["tools"] },
    { id: 9, source: "Email", sender: "Notion", content: "Notion: Weekly workspace summary is ready. You had 3 new pages shared.", timestamp: "9:05 AM", tags: ["tools"] },
    { id: 10, source: "Email", sender: "Notion", content: "Notion: Weekly workspace summary is ready. You had 3 new pages shared.", timestamp: "9:05 AM", tags: ["tools"] },
    { id: 11, source: "Email", sender: "Notion", content: "Notion: Weekly workspace summary is ready. You had 3 new pages shared.", timestamp: "9:05 AM", tags: ["tools"] },
  ]);
  const [isLeftPaneOpen, setIsLeftPaneOpen] = useState(true);
  const [expandedMsgId, setExpandedMsgId] = useState(null);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if ((e.metaKey || e.ctrlKey) && e.key === "k") {
        e.preventDefault();
        setIsPaletteOpen((prev) => !prev);
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  const tagColor = (tag) => {
    const map = {
      work: "bg-gray-100 text-gray-700",
      personal: "bg-pink-100 text-pink-700",
      urgent: "bg-red-100 text-red-700",
      design: "bg-orange-100 text-orange-700",
      payments: "bg-green-100 text-green-700",
      schedule: "bg-indigo-100 text-indigo-700",
      tools: "bg-gray-200 text-gray-700"
    };
    return map[tag] || "bg-gray-100 text-gray-600";
  };

  const allTags = [...new Set(messages.flatMap((m) => m.tags))];
  const allSources = [...new Set(messages.map((m) => m.source))];
  const filtered = messages.filter((m) => (filter.source === "All" || m.source === filter.source) && (filter.tag === "All" || m.tags.includes(filter.tag)));

  const deleteMessage = (id) => setMessages((msgs) => msgs.filter((m) => m.id !== id));

  return (
    <div className={`${darkMode ? "dark bg-gray-900 text-white" : "bg-gray-50 text-black"} h-screen w-screen flex flex-col font-mono relative`}>
      <div className="absolute top-4 right-6">
        <button onClick={() => setDarkMode(d => !d)} className="p-2 rounded hover:bg-gray-200">
          {darkMode ? <Sun className="w-4 h-4" /> : <Moon className="w-4 h-4" />}
        </button>
      </div>
      <div className="relative h-full flex-1">
        {/* Left Pane */}
        <div className={`fixed top-0 left-0 h-full flex flex-col bg-white border-r border-gray-200 z-20 transition-all duration-200 ${isLeftPaneOpen ? 'w-60' : 'w-12'}`}>
          <div className="flex items-center justify-between p-4 border-b border-gray-100">
            {isLeftPaneOpen && <div className="font-semibold text-sm text-gray-500 uppercase">Lists</div>}
            <button onClick={() => setIsLeftPaneOpen(open => !open)} className="ml-auto p-1 rounded hover:bg-gray-100">
              {isLeftPaneOpen ? <ChevronLeft className="w-5 h-5" /> : <ChevronRight className="w-5 h-5" />}
            </button>
          </div>
          <div className="flex-1 space-y-3 p-4">
            {isLeftPaneOpen ? (
              lists.map((list, i) => (
                <div key={i} className="p-2 rounded hover:bg-gray-100 cursor-pointer text-sm">
                  {list}
                </div>
              ))
            ) : (
              lists.map((_, i) => (
                <div key={i} className="w-6 h-6 rounded bg-gray-100 mx-auto my-2" />
              ))
            )}
          </div>
        </div>
        {/* Centered Middle Pane */}
        <div className="flex justify-center items-start min-h-screen">
          <div className="w-full max-w-2xl pt-8">
            <div className="mb-4 flex gap-4 text-sm px-4">
              <select onChange={(e) => setFilter(f => ({ ...f, source: e.target.value }))} className="p-2 border rounded text-black">
                <option value="All">All Platforms</option>
                {allSources.map(s => <option key={s}>{s}</option>)}
              </select>
              <select onChange={(e) => setFilter(f => ({ ...f, tag: e.target.value }))} className="p-2 border rounded text-black">
                <option value="All">All Tags</option>
                {allTags.map(t => <option key={t}>{t}</option>)}
              </select>
            </div>
            <div className="w-full px-4 overflow-y-auto space-y-4 transition-all duration-200">
              {filtered.map((msg) => (
                <div key={msg.id}>
                  <div className={`border border-gray-200 p-4 bg-white relative hover:bg-gray-50 cursor-pointer group ${expandedMsgId === msg.id ? 'rounded-t-xl rounded-b-none' : 'rounded-xl'}`}
                    onClick={() => setExpandedMsgId(expandedMsgId === msg.id ? null : msg.id)}>
                    <div className="flex justify-between text-xs text-gray-400">
                      <span>{msg.source}</span>
                      <span>{msg.timestamp}</span>
                    </div>
                    <div className="text-sm mt-1 whitespace-pre-wrap">{msg.content}</div>
                    <div className="mt-2 flex flex-wrap gap-2">
                      {msg.tags.map((tag, i) => (
                        <span key={i} className={`text-xs px-2 py-1 rounded-full ${tagColor(tag)}`}>{tag}</span>
                      ))}
                    </div>
                    <div className="absolute bottom-2 right-2 flex gap-2 opacity-0 group-hover:opacity-100">
                      <button onClick={e => { e.stopPropagation(); deleteMessage(msg.id); }} className="text-red-500 hover:text-red-700">
                        <Trash2 className="w-4 h-4" />
                      </button>
                      <button onClick={e => { e.stopPropagation(); alert('Choose list to add'); }}> <Plus className="w-4 h-4 text-gray-500" /></button>
                    </div>
                  </div>
                  {expandedMsgId === msg.id && (
                    <div className="border border-t-0 border-gray-200 rounded-b-xl bg-white px-4 py-4 flex flex-col gap-2">
                      <textarea className="w-full h-24 p-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-1 focus:ring-gray-300 text-black bg-white" placeholder="Give a command or respond..." />
                      <div className="flex justify-end">
                        <button className="mt-2 p-2 rounded-full bg-black hover:bg-gray-800 flex items-center justify-center text-white">
                          <svg className="w-5 h-5" viewBox="0 0 24 24" fill="white" xmlns="http://www.w3.org/2000/svg">
                            <circle cx="12" cy="12" r="12" fill="black" />
                            <path d="M12 8V16M12 8L8 12M12 8L16 12" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                          </svg>
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
        {/* Right Pane */}
        {selectedMsg && (
          <div className="fixed top-0 right-0 h-full w-1/3 border-l bg-white flex flex-col z-30">
            <div className="p-4 border-b flex justify-between items-center">
              <div>
                <div className="text-xs text-gray-400">{selectedMsg.source} – {selectedMsg.timestamp}</div>
                <div className="text-sm mt-1 whitespace-pre-wrap">{selectedMsg.content}</div>
                <div className="mt-2 flex flex-wrap gap-2">
                  {selectedMsg.tags.map((tag, i) => (
                    <span key={i} className={`text-xs px-2 py-1 rounded-full ${tagColor(tag)}`}>{tag}</span>
                  ))}
                </div>
              </div>
              <button onClick={() => setSelectedMsg(null)} className="text-gray-400 hover:text-red-500">
                <X className="w-4 h-4" />
              </button>
            </div>
            <div className="p-4 flex-1">
              <textarea className="w-full h-32 p-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-1 focus:ring-gray-300 text-black bg-white" placeholder="Give a command or respond..." />
              <button className="mt-2 p-2 rounded-full bg-black hover:bg-gray-800 flex items-center justify-center">
                <svg className="w-5 h-5" viewBox="0 0 24 24" fill="white" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="12" cy="12" r="12" fill="black" />
                  <path d="M12 8V16M12 8L8 12M12 8L16 12" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </button>
            </div>
          </div>
        )}
      </div>
      {/* Bottom Command Bar */}
      <div className="fixed bottom-4 left-1/2 transform -translate-x-1/2 w-full flex justify-center z-50">
        <div className="w-full max-w-2xl mx-auto bg-white border border-gray-200 shadow-md rounded-xl px-4 py-3 flex items-center gap-3">
          <input
            type="text"
            value={command}
            onChange={(e) => setCommand(e.target.value)}
            placeholder="Ask or command anything (e.g. 'Show me the message about Stripe payments')"
            className="flex-1 p-3 border rounded-md focus:outline-none focus:ring text-black"
          />
          <button className="p-2 rounded hover:bg-gray-100">
            <Mic className="w-5 h-5 text-gray-500" />
          </button>
          <button className="p-2 rounded-full bg-black hover:bg-gray-800 flex items-center justify-center">
            <svg className="w-5 h-5" viewBox="0 0 24 24" fill="white" xmlns="http://www.w3.org/2000/svg">
              <circle cx="12" cy="12" r="12" fill="black" />
              <path d="M12 8V16M12 8L8 12M12 8L16 12" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}
